	  /*****************************************************************************************************/
	  /*THIS SECTION CAPUTRES ON THOSE DATE FIELDS WHICH HAVE TO BE CONCATINATED WITH OTHER RELATED FIELDS*/
	  /*****************************************************************************************************/
INSERT INTO #GeneratedText 
	SELECT DISTINCT D.DEATH_REC_ID,D.INTERNAL_CASE_NUMBER,D.SFN_NUM,ad.AUDIT_RECORD_DETAILS_ID
	,convert(varchar,Year(d.DOD)) + convert(varchar,d.SFN_NUM) + '0'+convert(CHAR(1),d.VRV_REC_REPLACE_NBR)+ convert(varchar,replace(d.DATE_OF_AMENDMENT,'/',''),101) +'0' + --94
			'|' + c.Field +  --95
			'|' + CASE WHEN c.Field = 'REGISTRATION_DEATH_REGISTRATION_EVENT_DATE_LONG' 
					THEN (CONVERT(VARCHAR(30),DATEADD(DAY,-2,CONVERT(DATETIME , dt.DOD)),112 )
					+ (CASE WHEN dt.CERT_DESIG = 'MEDICAL EXAMINER' THEN
						CAST((CASE WHEN dt.TOD_IN_ME = 'P' AND dt.TOD_ME NOT LIKE '12:%' THEN CAST(CAST(LEFT(REPLACE(dt.TOD_ME,':',''),2) AS INT)+12 AS VARCHAR(100)) +CAST(RIGHT(REPLACE(dt.TOD_ME,':',''),2) AS VARCHAR(100))
							WHEN dt.TOD_IN_ME = 'D' AND dt.TOD_ME = '12:00' THEN '0000'
							ELSE REPLACE(ISNULL(dt.TOD_ME,'6666'),':','') 
							END) AS VARCHAR(10))
							WHEN dt.TOD_IN = 'D' AND dt.TOD = '12:00' THEN '0000'
							ELSE CAST((CASE WHEN dt.TOD_IN = 'P'  AND dt.TOD NOT LIKE '12:%' THEN CAST(CAST(LEFT(REPLACE(dt.TOD,':',''),2) AS INT)+12 AS VARCHAR(100)) +CAST(RIGHT(REPLACE(dt.TOD,':',''),2) AS VARCHAR(100))
							ELSE REPLACE(ISNULL(dt.TOD,'6666'),':','') END) AS VARCHAR(10))
						END))
					  WHEN c.Field = 'REGISTRATION_DEATH_INJURY_INJURY_DATE_LONG' 
					  THEN (CASE WHEN dt.DOI = '99/99/9999' THEN ''
								 WHEN dt.DOI IS NULL THEN ''
								 WHEN ISDATE(dt.DOI)= 0 THEN  REPLACE ((RIGHT(dt.DOI,4) + LEFT(dt.DOI,6)),'/','') + REPLACE(ISNULL(dt.TOI,'6666'),':','') 
								 WHEN ISDATE(dt.DOI) = 1 THEN ISNULL(CONVERT(VARCHAR(30),DATEADD(DAY,-2,CONVERT (DATETIME, dt.DOI)),112),'')  + REPLACE(ISNULL(dt.TOI,'6666'),':','')
							END)

				 END + --96 
			'|' + + CASE WHEN c.Field = 'REGISTRATION_DEATH_REGISTRATION_EVENT_DATE_LONG' 
					THEN (CONVERT(VARCHAR(30),DATEADD(DAY,-2,CONVERT(DATETIME , d.DOD)),112 )
					+ (CASE WHEN d.CERT_DESIG = 'MEDICAL EXAMINER' THEN
						CAST((CASE WHEN d.TOD_IN_ME = 'P' AND d.TOD_ME NOT LIKE '12:%' THEN CAST(CAST(LEFT(REPLACE(d.TOD_ME,':',''),2) AS INT)+12 AS VARCHAR(100)) +CAST(RIGHT(REPLACE(d.TOD_ME,':',''),2) AS VARCHAR(100))
							WHEN d.TOD_IN_ME = 'D' AND d.TOD_ME = '12:00' THEN '0000'
							ELSE REPLACE(ISNULL(d.TOD_ME,'6666'),':','') 
							END) AS VARCHAR(10))
							WHEN d.TOD_IN = 'D' AND d.TOD = '12:00' THEN '0000'
							ELSE CAST((CASE WHEN d.TOD_IN = 'P'  AND d.TOD NOT LIKE '12:%' THEN CAST(CAST(LEFT(REPLACE(d.TOD,':',''),2) AS INT)+12 AS VARCHAR(100)) +CAST(RIGHT(REPLACE(d.TOD,':',''),2) AS VARCHAR(100))
							ELSE REPLACE(ISNULL(d.TOD,'6666'),':','') END) AS VARCHAR(10))
						END))
					  WHEN c.Field = 'REGISTRATION_DEATH_INJURY_INJURY_DATE_LONG' 
					  THEN (CASE WHEN d.DOI = '99/99/9999' THEN ''
								 WHEN d.DOI IS NULL THEN ''
								 WHEN ISDATE(d.DOI)= 0 THEN  REPLACE ((RIGHT(d.DOI,4) + LEFT(d.DOI,6)),'/','') + REPLACE(ISNULL(d.TOI,'6666'),':','') 
								 WHEN ISDATE(d.DOI) = 1 THEN ISNULL(CONVERT(VARCHAR(30),DATEADD(DAY,-2,CONVERT (DATETIME, d.DOI)),112),'')  + REPLACE(ISNULL(d.TOI,'6666'),':','')
							END)

				 END + --97
			'|' + --98
			'|'  --99
	FROM [DAVE_MIGRATION].[vip_VRV_DEATH_TBL] d
